import "./App.css";
import { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faX } from "@fortawesome/free-solid-svg-icons";
import { v4 as uuidv4 } from "uuid";

function App() {
  return (
    <div id="main">
      <h1 className="main-title"> To-Do List</h1>
      <List />
    </div>
  );
}

function List() {
  const [toDo, changeToDo] = useState([]);

  const deleteTask = (event) => {
    let task = event.target;
    changeToDo(toDo.filter((item) => item.id !== task.id));
    task.nextElementSibling.remove();
  };

  const completeTask = (event) => {
    let item = document.querySelector(event.target);
    item.style.textDecoration = "line-through";
  };

  return (
    <div>
      <Input toDo={toDo} changeToDo={changeToDo} />
      <ul>
        {toDo.map((item) => (
          <div className="list-items">
            <li id={item.id}>{item.name}</li>
            <FontAwesomeIcon
              className="remove"
              icon={faX}
              onClick={deleteTask}
            />
          </div>
        ))}
      </ul>
      <TaskLength toDo={toDo} />
    </div>
  );
}

function Input({ toDo, changeToDo }) {
  const [taskText, setTaskText] = useState("");

  const changeText = (event) => {
    setTaskText(event.target.value);
  };

  const addTask = () => {
    let newTask = { name: taskText, id: uuidv4() };
    changeToDo([newTask, ...toDo]);
    clearInput();
  };

  const clearInput = () => {
    let input = document.querySelector("#input");
    input.value = "";
  };

  // Allows user to use enter to add task as well as pressing add button.
  useEffect(() => {
    let inputField = document.querySelector("#input");
    inputField.addEventListener("keypress", function (event) {
      if (event.key === "Enter") {
        addTask();
      }
    });
  });

  return (
    <div>
      <input
        type="text"
        name="input"
        id="input"
        onChange={changeText}
        placeholder="New Task..."
      />
      <button className="add-task" onClick={addTask}>
        Add
      </button>
    </div>
  );
}

function TaskLength({ toDo }) {
  return (
    <div>
      <h3 className="num-tasks">
        You have <span className="task-number">{toDo.length}</span> tasks.
      </h3>
    </div>
  );
}

export default App;
