import "./App.css";
import { useState } from "react";

function App() {
  return (
    <div id="main">
      <h1> To-Do List</h1>
      <List />
    </div>
  );
}

function List() {
  let toDo = ["laundry"];
  return (
    <div>
      <Input toDo={toDo} />
      <ul>
        {toDo.map((item) => (
          <li key={item}>{item}</li>
        ))}
      </ul>
    </div>
  );
}

function Input({ toDo }) {
  const addTask = () => {
    let newTask = document.querySelector("#input").nodeValue;
    toDo.unshift(newTask);
  };
  return (
    <div>
      <label htmlFor="input">New Task:</label>
      <input type="text" name="input" id="input" />
      <button className="add-task" onClick={addTask}>
        Add
      </button>
    </div>
  );
}

export default App;
