import "./App.css";
import { useState } from "react";

function App() {
  return (
    <div id="main">
      <h1> To-Do List</h1>
      <List />
    </div>
  );
}

function List() {
  let toDo = ["laundry", "coding"];

  return (
    <ul>
      {toDo.map((item) => (
        <li key={item}>{item}</li>
      ))}
    </ul>
  );
}

function Input() {
  const addItem = (EventTarget) => {
    toDo.unshift(item);
  };
  return (
    <div>
      <label htmlFor="input">New Task:</label>
      <input type="text" name="input" />
      <button className="add-task" onClick={addItem}>
        Save
      </button>
    </div>
  );
}

export default App;
