import "./App.css";
import { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faX } from "@fortawesome/free-solid-svg-icons";

function App() {
  return (
    <div id="main">
      <h1> To-Do List</h1>
      <List />
    </div>
  );
}

function List() {
  const [toDo, changeToDo] = useState(["laundry"]);
  const deleteTask = () => {
    let item = document.getElements;
  };

  return (
    <div>
      <Input toDo={toDo} changeToDo={changeToDo} />
      <ul>
        {toDo.map((item) => (
          <div className="list-items">
            <li key={item.id}>{item}</li>
            <FontAwesomeIcon icon={faX} onClick={deleteTask} />
          </div>
        ))}
      </ul>
      <TaskLength toDo={toDo} />
    </div>
  );
}

function Input({ toDo, changeToDo }) {
  const [taskText, setTaskText] = useState("");

  const changeText = (event) => {
    setTaskText(event.target.value);
  };

  const addTask = () => {
    changeToDo([taskText, ...toDo]);
    clearInput();
  };

  const clearInput = () => {
    let input = document.querySelector("#input");
    input.value = "";
  };

  // Allows user to use enter to add task as well as pressing add button.
  useEffect(() => {
    let inputField = document.querySelector("#input");
    inputField.addEventListener("keypress", function (event) {
      if (event.key === "Enter") {
        addTask();
      }
    });
  });

  return (
    <div>
      <label htmlFor="input">New Task:</label>
      <input type="text" name="input" id="input" onChange={changeText} />
      <button className="add-task" onClick={addTask}>
        Add
      </button>
    </div>
  );
}

function TaskLength({ toDo }) {
  return (
    <div>
      <h2>You have {toDo.length} tasks.</h2>
    </div>
  );
}

export default App;
